import React, { useState, useEffect } from 'react';
import authService from '../../services/authService';

import CrearTarea from './CrearTarea';
import SubirRecurso from './SubirRecurso';
import CalificarTarea from './CalificarTarea';
import TomarAsistencia from './TomarAsistencia';
import AsignaturasDisponibles from './AsignaturasDisponibles';
import './profesorCss/profesor.css';


const ProfesorDashboard = () => {
    const [user, setUser] = useState(null);
    const [selectedOption, setSelectedOption] = useState('');
    const [asignaturas, setAsignaturas] = useState([]);
    const [asignaturaSeleccionada, setAsignaturaSeleccionada] = useState(null);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const userData = await authService.getUserData(); // Función para obtener datos del usuario
                setUser(userData);
            } catch (error) {
                console.error("Error al obtener datos del usuario:", error);
                // Manejar el error, por ejemplo, redirigiendo al login
            }
        };
        fetchUserData();
    }, []);

    const handleMenuClick = (option) => {
        setSelectedOption(option);
    };

    if (!user) {
        return <div>Cargando...</div>;
    }

    return (
        <div className="dashboard-container">
            <div className="menu-container">
                <div className="menu-item" onClick={() => handleMenuClick('crearTarea')}>
                    Crear Tarea
                </div>
                <div className="menu-item" onClick={() => handleMenuClick('subirRecurso')}>
                    Subir Recurso
                </div>
                <div className="menu-item" onClick={() => handleMenuClick('calificarTarea')}>
                    Calificar Tarea
                </div>
                <div className="menu-item" onClick={() => handleMenuClick('tomarAsistencia')}>
                    Tomar Asistencia
                </div>
                <div className="menu-item" onClick={() => handleMenuClick('asignaturasDisponibles')}>
                    Asignaturas Disponibles
                </div>
            </div>
            <div className="content-container">
                {selectedOption === 'crearTarea' && <CrearTarea />}
                {selectedOption === 'subirRecurso' && <SubirRecurso />}
                {selectedOption === 'calificarTarea' && <CalificarTarea />}
                {selectedOption === 'tomarAsistencia' && <TomarAsistencia />}
                {selectedOption === 'asignaturasDisponibles' && <AsignaturasDisponibles />}
            </div>
        </div>
    );
};

export default ProfesorDashboard;
