import React, { useEffect, useState } from 'react';
import promediosService from '../../services/promediosService';

const Promedios = () => {
    const [promedios, setPromedios] = useState([]);

    useEffect(() => {
        const fetchPromedios = async () => {
            try {
                const promediosData = await promediosService.obtenerPromedios();
                setPromedios(promediosData);
            } catch (error) {
                console.error("Error al obtener los promedios:", error);
            }
        };
        fetchPromedios();
    }, []);

    if (!promedios.length) {
        return <div>Cargando...</div>;
    }

    return (
        <div>
            <h2>Promedios</h2>
            <ul>
                {promedios.map((promedio, index) => (
                    <li key={index}>{promedio.asignatura}: {promedio.promedio}</li>
                ))}
            </ul>
        </div>
    );
};

export default Promedios;
