import React, { useState, useEffect } from 'react';
import profesorService from '../../services/profesorService';
import { Card, Button, CardTitle, CardText, CardBody } from 'reactstrap';
import './profesorCss/verCursos.css';

const VerCursos = ({ setSubSubSelectedOption }) => {
    const [cursos, setCursos] = useState([]);

    useEffect(() => {
        const fetchCursos = async () => {
            try {
                const cursosData = await profesorService.obtenerCursosProfesor();
                setCursos(cursosData);
            } catch (error) {
                console.error("Error al obtener los cursos:", error);
            }
        };
        fetchCursos();
    }, []);

    const handleVerCurso = (curso) => {
        setSubSubSelectedOption('verCurso', curso.asignatura.id);
    };

    return (
        <div className="cursos-container">
            <h2>Mis Cursos</h2>
            <div className="cursos-grid">
                {cursos && cursos.length > 0 ? (
                    cursos.map((curso) => (
                        <Card key={curso.id} className="curso-card">
                            <CardBody>
                                <CardTitle tag="h5">{curso.asignatura?.nombre || 'Sin nombre'}</CardTitle>
                                <CardText>
                                    Profesor: {curso.asignatura?.profesor?.nombre || 'Sin profesor'}<br />
                                    Curso: {curso.curso?.nombre || 'Sin nombre'} - {curso.curso?.grado || 'Sin grado'}<br />
                                    Colegio: {curso.curso?.colegio?.nombre || 'Sin colegio'}
                                </CardText>
                                <Button onClick={() => handleVerCurso(curso)}>Ver Curso</Button>
                            </CardBody>
                        </Card>
                    ))
                ) : (
                    <p>No hay cursos disponibles.</p>
                )}
            </div>
        </div>
    );
};

export default VerCursos;
