import React, { useState, useEffect } from 'react';
import profesorService from '../../services/profesorService';

const VerForos = ({ idAsignatura }) => {
    const [foros, setForos] = useState([]);

    useEffect(() => {
        const fetchForos = async () => {
            try {
                const forosData = await profesorService.obtenerForos(idAsignatura);
                setForos(forosData);
            } catch (error) {
                console.error("Error al obtener los foros:", error);
            }
        };
        fetchForos();
    }, [idAsignatura]);

    return (
        <div>
            <h2>Foros</h2>
            <ul>
                {foros.map((foro) => (
                    <li key={foro.id}>{foro.titulo}</li>
                ))}
            </ul>
        </div>
    );
};

export default VerForos;
