import React, { useState } from 'react';
import profesorService from '../../services/profesorService';

const CrearTarea = ({ idCursoAsignatura }) => {
    const [descripcion, setDescripcion] = useState('');
    const [fechaPublicacion, setFechaPublicacion] = useState('');
    const [fechaEntrega, setFechaEntrega] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await profesorService.crearTarea(idCursoAsignatura, { descripcion, fechaPublicacion, fechaEntrega });
            // Reset form fields after submission
            setDescripcion('');
            setFechaPublicacion('');
            setFechaEntrega('');
        } catch (error) {
            console.error("Error al crear la tarea:", error);
        }
    };

    return (
        <div>
            <h2>Crear Tarea</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Descripción:</label>
                    <input type="text" value={descripcion} onChange={(e) => setDescripcion(e.target.value)} />
                </div>
                <div>
                    <label>Fecha de Publicación:</label>
                    <input type="date" value={fechaPublicacion} onChange={(e) => setFechaPublicacion(e.target.value)} />
                </div>
                <div>
                    <label>Fecha de Entrega:</label>
                    <input type="date" value={fechaEntrega} onChange={(e) => setFechaEntrega(e.target.value)} />
                </div>
                <button type="submit">Crear Tarea</button>
            </form>
        </div>
    );
};

export default CrearTarea;
