// LMS/lms_renca/src/components/administradorService.js
import axios from 'axios';
axios.defaults.xsrfCookieName = 'csrftoken';
axios.defaults.xsrfHeaderName = 'X-CSRFToken';

const API_BASE_URL = 'http://localhost:8000/api/municipalidades/';

export const crearColegio = async (data) => {
  try {
    const response = await axios.post(API_BASE_URL + 'crear_colegio/', data, {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`, // Asegúrate de que el token esté en el almacenamiento local o el contexto de tu app
      }
    });
    return response.data; // Mensaje de éxito
  } catch (error) {
    console.error('Error creando colegio:', error);
    throw error.response ? error.response.data : error;
  }
};

export const crearDirector = async (data) => {
  try {
    const response = await axios.post(API_BASE_URL + 'crear_director/', data, {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`, // Token para autorización
      }
    });
    return response.data; // Mensaje de éxito
  } catch (error) {
    console.error('Error creando director:', error);
    throw error.response ? error.response.data : error;
  }
};

export const obtenerMunicipalidades = async () => {
  try {
    const response = await axios.get(API_BASE_URL + 'obtener_municipalidades/', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`, // Token para autorización
      }
    });
    return response.data.municipalidades; // Devuelve las municipalidades
  } catch (error) {
    console.error('Error obteniendo municipalidades:', error);
    throw error.response ? error.response.data : error;
  }
};

export const obtenerColegios = async () => {
