    console.error('Error obteniendo colegios:', error);
    throw error.response ? error.response.data : error;
  }
};

export const obtenerDirectores = async () => {
  try {
    const response = await axios.get(API_BASE_URL + 'obtener_directores/', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`, // Token para autorización
      }
    });
    return response.data.directores; // Devuelve los directores
  } catch (error) {
    console.error('Error obteniendo directores:', error);
    throw error.response ? error.response.data : error;
  }
};

export const obtenerCursos = async () => {
  try {
    const response = await axios.get(API_BASE_URL + 'obtener_cursos/', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`, // Token para autorización
      }
    });
    return response.data.cursos; // Devuelve los cursos
  } catch (error) {
    console.error('Error obteniendo cursos:', error);
    throw error.response ? error.response.data : error;
  }
};

export const obtenerAsignaturas = async () => {
  try {
    const response = await axios.get(API_BASE_URL + 'obtener_asignaturas/', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`, // Token para autorización
      }
    });
    return response.data.asignaturas; // Devuelve las asignaturas
  } catch (error) {
    console.error('Error obteniendo asignaturas:', error);
    throw error.response ? error.response.data : error;
  }
};

export const obtenerInscripciones = async () => {
  try {
    const response = await axios.get(API_BASE_URL + 'obtener_incripciones/', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('token')}`, // Token para autorización
      }
    });
    return response.data.inscripciones; // Devuelve las inscripciones
  } catch (error) {
    console.error('Error obteniendo inscripciones:', error);
    throw error.response ? error.response.data : error;
  }
};

const AdministradorService = {
  crearColegio,
  crearDirector,
  obtenerMunicipalidades,
  obtenerColegios,
  obtenerDirectores,
  obtenerCursos,
  obtenerAsignaturas,
  obtenerInscripciones
}

export default AdministradorService;
