//LMS/lms_renca_frontend/src/components/profesor/TomarAsistencia.js
import React, { useState, useEffect } from 'react';
import tomarAsistencia from '../../services/profesorService';

const TomarAsistencia = () => {
    const [asistencia, setAsistencia] = useState({
        cursoId: '',
        fecha: '',
        alumnos: [],
    });
    
    useEffect(() => {
        // Llamada a la API para obtener los alumnos del curso
        // y guardarlos en el estado
    }, []);
    
    const handleChange = (event) => {
        const { name, value } = event.target;
        setAsistencia({
        ...asistencia,
        [name]: value,
        });
    };
    
    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
        // Llamada a la API para registrar la asistencia
        await tomarAsistencia(asistencia);
        alert('Asistencia registrada correctamente');
        } catch (error) {
        alert(error.message);
        }
    };
    
    return (
        <div>
        <h1>Tomar asistencia</h1>
        <form onSubmit={handleSubmit}>
            <label>
            Curso:
            <input
                type="text"
                name="cursoId"
                value={asistencia.cursoId}
                onChange={handleChange}
            />
            </label>
            <label>
            Fecha:
            <input
                type="date"
                name="fecha"
                value={asistencia.fecha}
                onChange={handleChange}
            />
            </label>
            <button type="submit">Tomar asistencia</button>
        </form>
        </div>
    );
};

export default TomarAsistencia;
