import React, { useState, useEffect } from 'react';
import profesorService from '../../services/profesorService';

const VerAsistencias = ({ idCursoAsignatura }) => {
    const [asistencias, setAsistencias] = useState([]);
    const [nuevaAsistencia, setNuevaAsistencia] = useState({ fecha: '', presentes: {} });

    useEffect(() => {
        const fetchAsistencias = async () => {
            try {
                const asistenciasData = await profesorService.obtenerAsistencias(idCursoAsignatura);
                setAsistencias(asistenciasData);
            } catch (error) {
                console.error("Error al obtener las asistencias:", error);
            }
        };
        fetchAsistencias();
    }, [idCursoAsignatura]);

    const handleFechaChange = (e) => {
        setNuevaAsistencia({ ...nuevaAsistencia, fecha: e.target.value });
    };

    const handlePresenteChange = (idEstudiante) => {
        setNuevaAsistencia((prevState) => ({
            ...prevState,
            presentes: {
                ...prevState.presentes,
                [idEstudiante]: !prevState.presentes[idEstudiante],
            }
        }));
    };

    const crearAsistencia = async () => {
        try {
            for (let idEstudiante in nuevaAsistencia.presentes) {
                await profesorService.tomarAsistencia(
                    idCursoAsignatura,
                    idEstudiante,
                    { fecha: nuevaAsistencia.fecha, presente: nuevaAsistencia.presentes[idEstudiante] }
                );
            }
            alert("Asistencias creadas exitosamente");
        } catch (error) {
            console.error("Error al crear las asistencias:", error);
        }
    };

    return (
        <div>
            <h2>Libro de Asistencias</h2>
            <div>
                <label>Fecha: </label>
                <input type="date" value={nuevaAsistencia.fecha} onChange={handleFechaChange} />
            </div>
            <table>
                <thead>
                    <tr>
                        <th>RUT</th>
                        <th>Nombre Apellido</th>
                        <th>Asistencia</th>
                    </tr>
                </thead>
                <tbody>
                    {asistencias.map((asistencia) => (
                        <tr key={asistencia.id}>
                            <td>{asistencia.estudiante.rut}</td>
                            <td>{asistencia.estudiante.nombre_apellido}</td>
                            <td>
                                <button
                                    style={{ backgroundColor: nuevaAsistencia.presentes[asistencia.estudiante.id] ? 'green' : 'red' }}
                                    onClick={() => handlePresenteChange(asistencia.estudiante.id)}
                                >
                                    {nuevaAsistencia.presentes[asistencia.estudiante.id] ? 'Presente' : 'Ausente'}
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={crearAsistencia}>Crear Asistencia</button>
        </div>
    );
};

export default VerAsistencias;
