//LMS/lms_renca_frontend/src/components/profesor/SubirRecurso.js
import React, { useState, useEffect } from 'react';
import profesorService from '../../services/profesorService';

const SubirRecurso = ({ asignaturaId }) => {
    const [titulo, setTitulo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [archivo, setArchivo] = useState(null);
    const [alumnos, setAlumnos] = useState([]);
    const [recursos, setRecursos] = useState([]);
    const [recurso, setRecurso] = useState({});

    useEffect(() => {
        profesorService.getAlumnos(asignaturaId).then((data) => {
            setAlumnos(data);
        });
    }, [asignaturaId]);

    useEffect(() => {
        profesorService.getRecursos(asignaturaId).then((data) => {
            setRecursos(data);
        });
    }, [asignaturaId]);

    const handleChange = (e, alumnoId) => {
        const { value } = e.target;
        setRecurso({ ...recurso, [alumnoId]: value });
    };

    const handleFileChange = (e) => {
        setArchivo(e.target.files[0]);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('titulo', titulo);
        formData.append('descripcion', descripcion);
        formData.append('archivo', archivo);
        formData.append('recurso', JSON.stringify(recurso));
        profesorService.subirRecurso(asignaturaId, formData).then(() => {
            alert('Recurso subido');
            setTitulo('');
            setDescripcion('');
            setArchivo(null);
            setRecurso({});
        });
    };

    return (
        <div>
            <h3>Subir Recurso</h3>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Título"
                    value={titulo}
                    onChange={(e) => setTitulo(e.target.value)}
                />
                <textarea
                    placeholder="Descripción"
                    value={descripcion}
                    onChange={(e) => setDescripcion(e.target.value)}
                />
                <input
                    type="file"
                    onChange={handleFileChange}
                />
                {alumnos.map((alumno) => (
                    <div key={alumno._id}>
                        <input
                            type="text"
                            placeholder={alumno.nombre}
                            value={recurso[alumno._id] || ''}
                            onChange={(e) => handleChange(e, alumno._id)}
                        />
                    </div>
                ))}
                <button type="submit">Subir Recurso</button>
            </form>
            <h3>Recursos</h3>
            {recursos.map((recurso) => (
                <div key={recurso._id}>
                    <h4>{recurso.titulo}</h4>
                    <p>{recurso.descripcion}</p>
                    <a href={`${process.env.REACT_APP_API_URL}/recursos/${recurso.archivo}`} download>Descargar</a>
                </div>
            ))}
        </div>
    );
};

export default SubirRecurso;
