import React, { useState, useEffect } from "react";
import directorService from "../../services/directorService";


function CrearAsignaturaForm() {
  const [nombre, setNombre] = useState("");
  const [cursoId, setCursoId] = useState("");  // Este campo es opcional si la asignatura está asociada a un curso
  const [cursos, setCursos] = useState([]);

  // Cargar los cursos disponibles cuando se monta el componente
  useEffect(() => {
    const fetchCursos = async () => {
      try {
        const cursosData = await directorService.obtenerCursos();
        setCursos(cursosData);
      } catch (error) {
        console.error("Error al obtener los cursos:", error);
        // Mostrar un mensaje al usuario si es necesario
      }
    };
    fetchCursos();
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      // Llamada al servicio para crear la asignatura
      await directorService.crearAsignatura({ nombre, curso_id: cursoId });
      
      // Limpiar los campos después de enviar el formulario
      setNombre("");
      setCursoId("");
      alert("Asignatura creada con éxito");
    } catch (error) {
      console.error("Error al crear la asignatura:", error);
      // Mostrar un mensaje de error si algo sale mal
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="nombre">Nombre:</label>
        <input
          type="text"
          id="nombre"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="cursoId">Curso:</label>
        <select
          id="cursoId"
          value={cursoId}
          onChange={(e) => setCursoId(e.target.value)}
        >
          <option value="">Selecciona un curso </option>
          {cursos.map((curso) => (
            <option key={curso.id} value={curso.id}>
              {curso.nombre}
            </option>
          ))}
        </select>
      </div>
      <button type="submit">Crear Asignatura</button>
    </form>
  );
}

export default CrearAsignaturaForm;
