import React, { useEffect, useState } from "react";
import EstudianteService from "../../services/estudianteService";
import { Bar } from "react-chartjs-2";

const ReportesDeProgreso = () => {
    const [reportes, setReportes] = useState([]);
    const estudianteRut = "12345678-9";

    useEffect(() => {
        const fetchReportes = async () => {
            try {
                const data = await EstudianteService.obtener_reportes_progreso(estudianteRut);
                setReportes(data);
            } catch (error) {
                console.error("Error al obtener reportes:", error);
            }
        };
        fetchReportes();
    }, []);

    const data = {
        labels: reportes.map((reporte) => reporte.asignatura),
        datasets: [
            {
                label: "Progreso",
                data: reportes.map((reporte) => reporte.porcentaje),
                backgroundColor: "rgba(75,192,192,0.6)",
            },
        ],
    };

    return (
        <div>
            <h2>Reportes de Progreso</h2>
            <Bar data={data} />
        </div>
    );
};

export default ReportesDeProgreso;
