//LMS/lms_renca_frontend/src/components/profesor/CrearTarea.js
import React, { useState, useEffect } from 'react';
import crearTarea from '../../services/profesorService';

const CrearTarea = ({ asignaturaId }) => {
    const [tarea, setTarea] = useState({
        titulo: '',
        descripcion: '',
        fechaEntrega: '',
    });
    const [mensaje, setMensaje] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setTarea({ ...tarea, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        crearTarea(asignaturaId, tarea).then(() => {
            setMensaje('Tarea creada correctamente');
            setTarea({
                titulo: '',
                descripcion: '',
                fechaEntrega: '',
            });
        });
    };

    return (
        <div>
            <h3>Crear Tarea</h3>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="titulo"
                    placeholder="Título"
                    value={tarea.titulo}
                    onChange={handleChange}
                />
                <textarea
                    name="descripcion"
                    placeholder="Descripción"
                    value={tarea.descripcion}
                    onChange={handleChange}
                ></textarea>
                <input
                    type="date"
                    name="fechaEntrega"
                    value={tarea.fechaEntrega}
                    onChange={handleChange}
                />
                <button type="submit">Crear Tarea</button>
            </form>
            {mensaje && <p>{mensaje}</p>}
        </div>
    );
};

export default CrearTarea;
