import React, { useEffect, useState } from "react";
import EstudianteService from "../../services/estudianteService";

const Recursos = () => {
    const [recursos, setRecursos] = useState([]);
    const estudianteRut = "12345678-9";
    const asignaturaId = "1"; // Cambiar según lógica del sistema.

    useEffect(() => {
        const fetchRecursos = async () => {
            try {
                const data = await EstudianteService.obtener_recursos(estudianteRut, asignaturaId);
                setRecursos(data);
            } catch (error) {
                console.error("Error al obtener recursos:", error);
            }
        };
        fetchRecursos();
    }, []);

    const descargarRecurso = async (recursoId) => {
        try {
            await EstudianteService.descargar_recurso(recursoId);
            alert("Recurso descargado con éxito.");
        } catch (error) {
            console.error("Error al descargar recurso:", error);
        }
    };

    return (
        <div>
            <h2>Recursos</h2>
            <ul>
                {recursos.map((recurso) => (
                    <li key={recurso.id}>
                        {recurso.nombre}{" "}
                        <button onClick={() => descargarRecurso(recurso.id)}>Descargar</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Recursos;
