//LMS/lms_renca_frontend/src/components/profesor/TomarAsistencia.js
import React, { useState, useEffect } from 'react';
import profesorService from '../../services/profesorService';

const TomarAsistencia = ({ asignaturaId }) => {
    const [alumnos, setAlumnos] = useState([]);
    const [asistencia, setAsistencia] = useState([]);

    useEffect(() => {
        profesorService.getAlumnos(asignaturaId).then((data) => {
            setAlumnos(data);
        });
    }, [asignaturaId]);

    const handleChange = (e, alumnoId) => {
        const { checked } = e.target;
        if (checked) {
            setAsistencia([...asistencia, alumnoId]);
        } else {
            setAsistencia(asistencia.filter((id) => id !== alumnoId));
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        profesorService.tomarAsistencia(asignaturaId, asistencia).then(() => {
            alert('Asistencia guardada');
            setAsistencia([]);
        });
    };

    return (
        <div>
            <h3>Tomar Asistencia</h3>
            <form onSubmit={handleSubmit}>
                {alumnos.map((alumno) => (
                    <div key={alumno._id}>
                        <input
                            type="checkbox"
                            id={alumno._id}
                            checked={asistencia.includes(alumno._id)}
                            onChange={(e) => handleChange(e, alumno._id)}
                        />
                        <label htmlFor={alumno._id}>{alumno.nombre}</label>
                    </div>
                ))}
                <button type="submit">Guardar Asistencia</button>
            </form>
        </div>
    );
};

export default TomarAsistencia;
