//LMS/lms_renca_frontend/src/components/profesor/SubirRecurso.js
import React, { useState, useEffect } from 'react';
import subirRecurso from '../../services/profesorService';

const SubirRecurso = ({ asignaturaId }) => {
    const [titulo, setTitulo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [archivo, setArchivo] = useState(null);

    const handleSubmit = (e) => {
        e.preventDefault();
        const datosRecurso = new FormData();
        datosRecurso.append('asignaturaId', asignaturaId);
        datosRecurso.append('titulo', titulo);
        datosRecurso.append('descripcion', descripcion);
        datosRecurso.append('archivo', archivo);

        subirRecurso(datosRecurso).then(() => {
            alert('Recurso subido');
            setTitulo('');
            setDescripcion('');
            setArchivo(null);
        });
    };

    return (
        <div>
            <h3>Subir Recurso</h3>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Título"
                    value={titulo}
                    onChange={(e) => setTitulo(e.target.value)}
                />
                <textarea
                    placeholder="Descripción"
                    value={descripcion}
                    onChange={(e) => setDescripcion(e.target.value)}
                />
                <input
                    type="file"
                    onChange={(e) => setArchivo(e.target.files[0])}
                />
                <button type="submit">Subir Recurso</button>
            </form>
        </div>
    );
};

export default SubirRecurso;
