import React, { useEffect, useState } from "react";
import EstudianteService from "../../services/estudianteService";

const CursosYAsignaturas = () => {
    const [data, setData] = useState([]);
    const estudianteRut = "12345678-9"; // Este valor debería obtenerse de la sesión del usuario.

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await EstudianteService.obtener_cursos_y_asignaturas(estudianteRut);
                setData(response);
            } catch (error) {
                console.error("Error al obtener cursos y asignaturas:", error);
            }
        };
        fetchData();
    }, []);

    return (
        <div>
            <h2>Cursos y Asignaturas</h2>
            <ul>
                {data.map((item) => (
                    <li key={item.id}>
                        {item.nombreCurso} - {item.nombreAsignatura}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default CursosYAsignaturas;
