import React, { useState, useEffect } from 'react';
import profesorService from '../../services/profesorService';

const TomarAsistencia = ({ asignaturaId }) => {
    const [alumnos, setAlumnos] = useState([]);
    const [asistencias, setAsistencias] = useState([]);

    useEffect(() => {
        profesorService.getAlumnos(asignaturaId).then((data) => {
            setAlumnos(data);
        });
    }, [asignaturaId]);

    const handleChange = (e, alumnoId) => {
        const { checked } = e.target;
        setAsistencias([...asistencias, { alumnoId, presente: checked }]);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        profesorService.tomarAsistencia(asignaturaId, asistencias).then(() => {
            alert('Asistencias guardadas');
            setAsistencias([]);
        });
    };

    return (
        <div>
            <h3>Tomar Asistencia</h3>
            <form onSubmit={handleSubmit}>
                {alumnos.map((alumno) => (
                    <div key={alumno._id}>
                        <h4>{alumno.nombre}</h4>
                        <input
                            type="checkbox"
                            onChange={(e) => handleChange(e, alumno._id)}
                        />
                    </div>
                ))}
                <button type="submit">Guardar Asistencias</button>
            </form>
        </div>
    );
};

export default TomarAsistencia;
