import React, { useState, useEffect } from "react";
import authService from "../../services/authService";
//import VerCurso from "./VerCurso";
//import VerHorario from "./VerHorario";
//import VerAsignaturas from "./VerAsignaturas";
//import MisNotas from "./MisNotas";
//import Asistencia from "./Asistencia";
//import Promedios from "./Promedios";
import './estudianteCss/estudiante.css';

const EstudianteDashboard = () => {
    const [user, setUser] = useState(null);
    const [selectedSubMenu, setSelectedSubMenu] = useState("");

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const userData = await authService.getUserData();
                setUser(userData);
            } catch (error) {
                console.error("Error al obtener datos del usuario:", error);
            }
        };
        fetchUserData();
    }, []);

    const handleSubMenuClick = (subMenu) => {
        setSelectedSubMenu(subMenu);
    };

    if (!user) {
        return <div>Cargando...</div>;
    }

    return (
        <div className="dashboard-container">
            <div className="header">
                <h1>LMS Renca</h1>
                <div>
                    {user && (
                        <div>
                            <span>{user.nombre} {user.apellido}</span>
                            <button onClick={() => authService.logout()}>Cerrar Sesión</button>
                        </div>
                    )}
                </div>
            </div>
            <nav className="nav-bar">
                <ul>
                    <li>
                        <button>Mi Curso</button>
                        <ul className="submenu">
                            <li><button onClick={() => handleSubMenuClick("verCurso")}>Ver Curso</button></li>
                            <li><button onClick={() => handleSubMenuClick("verHorario")}>Ver Horario</button></li>
                            <li><button onClick={() => handleSubMenuClick("verAsignaturas")}>Ver Asignaturas</button></li>
                        </ul>
                    </li>
                    <li>
                        <button onClick={() => handleSubMenuClick("notas")}>Mis Notas</button>
                    </li>
                    <li>
                        <button>Mis Resultados</button>
                        <ul className="submenu">
                            <li><button onClick={() => handleSubMenuClick("asistencia")}>Asistencia</button></li>
                            <li><button onClick={() => handleSubMenuClick("promedios")}>Promedio</button></li>
                        </ul>
                    </li>
                </ul>
            </nav>
            <div className="content">
                {selectedSubMenu === "verCurso" }
                {selectedSubMenu === "verHorario" }
                {selectedSubMenu === "verAsignaturas" }
                {selectedSubMenu === "notas" }
                {selectedSubMenu === "asistencia" }
                {selectedSubMenu === "promedios" }
            </div>
        </div>
    );
};

export default EstudianteDashboard;
