// LMS/lms_renca/src/components/administradorService.js
import axios from 'axios';


const API_BASE_URL = 'http://localhost:8000/api/municipalidades/';

const authHeader = () => {
  const token = localStorage.getItem('token');
  if (token) {
    return { Authorization: `Bearer ${token}` };
  }
  return {};
};

export const AdministradorService = {

  //funcion que obtiene los colegios
  obtenerColegios: async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}obtener_colegios/`, { headers: authHeader() });
      return response.data;
    } catch (error) {
      console.error("Error al obtener los colegios:", error);
      throw error;
    }
  },


};
