//LMS/lms_renca_frontend/src/components/profesor/SubirRecurso.js
import React, { useState, useEffect } from 'react';
import subirRecurso from '../../services/profesorService';

const SubirRecurso = ({ asignaturaId }) => {
    const [recurso, setRecurso] = useState(null);
    const [mensaje, setMensaje] = useState('');

    const handleChange = (e) => {
        setRecurso(e.target.files[0]);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('recurso', recurso);
        formData.append('asignaturaId', asignaturaId);
        subirRecurso(formData).then(() => {
            setMensaje('Recurso subido correctamente');
        });
    };

    return (
        <div>
            <h3>Subir Recurso</h3>
            <form onSubmit={handleSubmit}>
                <input type="file" onChange={handleChange} />
                <button type="submit">Subir Recurso</button>
            </form>
            {mensaje && <p>{mensaje}</p>}
        </div>
    );
};

export default SubirRecurso;
