//LMS/lms_renca_frontend/src/components/profesor/CalificarTarea.js
import React, { useState, useEffect } from 'react';
import calificarTarea from '../../services/profesorService';

const CalificarTarea = ({ tareaId }) => {
    const [calificaciones, setCalificaciones] = useState([]);
    const [nota, setNota] = useState(0);

    useEffect(() => {
        calificarTarea.getCalificaciones(tareaId).then((data) => {
            setCalificaciones(data);
        });
    }, [tareaId]);

    const handleChange = (e, alumnoId) => {
        const { value } = e.target;
        setCalificaciones({
            ...calificaciones,
            [alumnoId]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        calificarTarea.calificarTarea(tareaId, calificaciones).then(() => {
            alert('Tarea calificada');
        });
    };

    return (
        <div>
            <h3>Calificar Tarea</h3>
            <form onSubmit={handleSubmit}>
                {Object.keys(calificaciones).map((alumnoId) => (
                    <div key={alumnoId}>
                        <label htmlFor={alumnoId}>{alumnoId}</label>
                        <input
                            type="number"
                            id={alumnoId}
                            value={calificaciones[alumnoId]}
                            onChange={(e) => handleChange(e, alumnoId)}
                        />
                    </div>
                ))}
                <button type="submit">Calificar Tarea</button>
            </form>
        </div>
    );
};

export default CalificarTarea;
