import React, { useState, useEffect } from 'react';
import profesorService from '../../services/profesorService';
import { Card, Button, CardTitle, CardText, CardBody } from 'reactstrap';
import './profesorCss/verCursos.css'; // Asegúrate de tener este archivo CSS para estilizar las tarjetas

const VerCursos = ({ setSubSubSelectedOption }) => {
    const [cursos, setCursos] = useState([]);

    useEffect(() => {
        const fetchCursos = async () => {
            try {
                const cursosData = await profesorService.obtenerCursoAsignatura();
                setCursos(cursosData.cursos);
            } catch (error) {
                console.error("Error al obtener los cursos:", error);
            }
        };
        fetchCursos();
    }, []);

    return (
        <div className="cursos-container">
            <h2>Mis Cursos</h2>
            <div className="cursos-grid">
                {cursos.map((curso) => (
                    <Card key={curso.id} className="curso-card">
                        <CardBody>
                            <CardTitle tag="h5">{curso.nombre}</CardTitle>
                            <CardText>
                                {curso.descripcion}
                            </CardText>
                            <Button onClick={() => setSubSubSelectedOption('recursos')}>Ver Recursos</Button>
                        </CardBody>
                    </Card>
                ))}
            </div>
        </div>
    );
};

export default VerCursos;
