//LMS/lms_renca_frontend/src/components/profesor/CrearTarea.js
import React, { useState, useEffect } from 'react';
import crearTarea from '../../services/profesorService';

const CrearTarea = ({ asignaturaId }) => {
    const [titulo, setTitulo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [fechaEntrega, setFechaEntrega] = useState('');
    const [alumnos, setAlumnos] = useState([]);
    const [tarea, setTarea] = useState({});

    useEffect(() => {
        crearTarea.getAlumnos(asignaturaId).then((data) => {
            setAlumnos(data);
        });
    }, [asignaturaId]);

    const handleChange = (e, alumnoId) => {
        const { value } = e.target;
        setTarea({ ...tarea, [alumnoId]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        crearTarea.crearTarea(asignaturaId, titulo, descripcion, fechaEntrega, tarea).then(() => {
            alert('Tarea creada');
            setTitulo('');
            setDescripcion('');
            setFechaEntrega('');
            setTarea({});
        });
    };

    return (
        <div>
            <h3>Crear Tarea</h3>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Título"
                    value={titulo}
                    onChange={(e) => setTitulo(e.target.value)}
                />
                <textarea
                    placeholder="Descripción"
                    value={descripcion}
                    onChange={(e) => setDescripcion(e.target.value)}
                />
                <input
                    type="date"
                    value={fechaEntrega}
                    onChange={(e) => setFechaEntrega(e.target.value)}
                />
                {alumnos.map((alumno) => (
                    <div key={alumno._id}>
                        <input
                            type="text"
                            placeholder={alumno.nombre}
                            value={tarea[alumno._id] || ''}
                            onChange={(e) => handleChange(e, alumno._id)}
                        />
                    </div>
                ))}
                <button type="submit">Crear Tarea</button>
            </form>
        </div>
    );
};

export default CrearTarea;
