import React, { useEffect, useState } from "react";
import EstudianteService from "../../services/estudianteService";

const Foros = () => {
    const [foros, setForos] = useState([]);
    const estudianteRut = "12345678-9";
    const asignaturaId = "1"; // Cambiar según lógica del sistema.

    useEffect(() => {
        const fetchForos = async () => {
            try {
                const data = await EstudianteService.obtener_foros(estudianteRut, asignaturaId);
                setForos(data);
            } catch (error) {
                console.error("Error al obtener foros:", error);
            }
        };
        fetchForos();
    }, []);

    return (
        <div>
            <h2>Foros</h2>
            <ul>
                {foros.map((foro) => (
                    <li key={foro.id}>
                        {foro.titulo}
                        <p>{foro.descripcion}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Foros;
