import React, { useState, useEffect } from 'react';
import profesorService from '../../services/profesorService';

const VerAsistencias = ({ idCursoAsignatura }) => {
    const [asistencias, setAsistencias] = useState([]);

    useEffect(() => {
        const fetchAsistencias = async () => {
            try {
                const asistenciasData = await profesorService.obtenerAsistencias(idCursoAsignatura);
                setAsistencias(asistenciasData);
            } catch (error) {
                console.error("Error al obtener las asistencias:", error);
            }
        };
        fetchAsistencias();
    }, [idCursoAsignatura]);

    return (
        <div>
            <h2>Asistencias</h2>
            <ul>
                {asistencias.map((asistencia) => (
                    <li key={asistencia.id}>{asistencia.fecha} - {asistencia.presente ? 'Presente' : 'Ausente'}</li>
                ))}
            </ul>
        </div>
    );
};

export default VerAsistencias;
