import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import profesorService from '../../services/profesorService';

const CrearTarea = () => {
    const { idCursoAsignatura } = useParams(); // Obtener idCursoAsignatura de los parámetros de la URL
    const [titulo, setTitulo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [fechaEntrega, setFechaEntrega] = useState('');
    const [archivo, setArchivo] = useState(null);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('titulo', titulo);
            formData.append('descripcion', descripcion);
            formData.append('fecha_publicacion', new Date().toISOString().slice(0, 10)); // Fecha actual
            formData.append('fecha_entrega', fechaEntrega);
            formData.append('archivo', archivo);

            await profesorService.crearTarea(idCursoAsignatura, formData);
            // Redirigir o mostrar un mensaje de éxito
            navigate(`/verTareas/${idCursoAsignatura}`);
        } catch (error) {
            console.error('Error al crear la tarea:', error);
            // Manejar el error
        }
    };

    return (
        <div>
            <h2>Crear Tarea</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Título:
                    <input type="text" value={titulo} onChange={(e) => setTitulo(e.target.value)} required />
                </label>
                <label>
                    Descripción:
                    <textarea value={descripcion} onChange={(e) => setDescripcion(e.target.value)} required></textarea>
                </label>
                <label>
                    Fecha de Entrega:
                    <input type="datetime-local" value={fechaEntrega} onChange={(e) => setFechaEntrega(e.target.value)} required />
                </label>
                <label>
                    Archivo:
                    <input type="file" onChange={(e) => setArchivo(e.target.files[0])} />
                </label>
                <button type="submit">Crear Tarea</button>
            </form>
        </div>
    );
};

export default CrearTarea;
