// LMS/lms_renca/src/components/administradorService.js
import axios from 'axios';

const BASE_URL = 'http://localhost:8000/municipalidad/';
const token = localStorage.getItem('token'); // or any other method to retrieve the token
axios.defaults.headers.common['Authorization'] = `Bearer ${token}`; // Asegúrate de establecer el token correctamente


const obtenerColegios = () => {
    return axios.get(`${BASE_URL}colegios/`)
        .then(response => response.data)
        .catch(error => {
            console.error(error);
            throw error;
        });
};

const crearColegio = (colegio) => {
    return axios.post(`${BASE_URL}colegios/`, colegio)
        .then(response => response.data)
        .catch(error => {
            console.error(error);
            throw error;
        });
};

const actualizarColegio = (id, colegio) => {
    return axios.put(`${BASE_URL}colegios/${id}/`, colegio)
        .then(response => response.data)
        .catch(error => {
            console.error(error);
            throw error;
        });
};

const eliminarColegio = (id) => {
    return axios.delete(`${BASE_URL}colegios/${id}/`)
        .then(response => response.data)
        .catch(error => {
            console.error(error);
            throw error;
        });
};

const obtenerDirectores = () => {
    return axios.get(`${BASE_URL}directores/`)
        .then(response => response.data)
        .catch(error => {
            console.error(error);
            throw error;
        });
};

const crearDirector = (director) => {
    return axios.post(`${BASE_URL}directores/`, director)
        .then(response => response.data)
        .catch(error => {
            console.error(error);
            throw error;
        });
};

const actualizarDirector = (id, director) => {
    return axios.put(`${BASE_URL}directores/${id}/`, director)
        .then(response => response.data)
        .catch(error => {
            console.error(error);
            throw error;
        });
};

const eliminarDirector = (id) => {
    return axios.delete(`${BASE_URL}directores/${id}/`)
        .then(response => response.data)
        .catch(error => {
            console.error(error);
            throw error;
        });
};

const obtenerResultados = () => {
    return axios.get(`${BASE_URL}resultados/`)
        .then(response => response.data)
        .catch(error => {
            console.error(error);
            throw error;
        });
};

const obtenerMunicipalidades = () => {
    return axios.get(`${BASE_URL}municipalidades/`)
        .then(response => response.data)
        .catch(error => {
            console.error(error);
            throw error;
        });
};

// Asegúrate de que obtenerMunicipalidades esté exportado correctamente
export {
    obtenerColegios,
    crearColegio,
    actualizarColegio,
    eliminarColegio,
    obtenerDirectores,
    crearDirector,
    actualizarDirector,
    eliminarDirector,
    obtenerResultados,
    obtenerMunicipalidades // Añade esta línea si falta
};
