import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { EstudianteService } from '../../services/estudianteService';
import { Card, CardBody, CardTitle, CardText, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import './estudianteCss/verForo.css';

const VerForo = ({ asignaturaId }) => {
    const [foros, setForos] = useState([]);
    const [comentarios, setComentarios] = useState({});
    const [nuevoComentario, setNuevoComentario] = useState('');
    const [fechaCreacionComentario, setFechaCreacionComentario] = useState('');
    const [selectedForoId, setSelectedForoId] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchForos = async () => {
            try {
                const response = await EstudianteService.obtener_foros(asignaturaId);
                setForos(response);
            } catch (error) {
                console.error("Error al obtener los foros:", error);
            }
        };
        fetchForos();
    }, [asignaturaId]);

    const fetchComentarios = async (foroId) => {
        try {
            const response = await EstudianteService.obtener_comentarios(foroId);
            setComentarios((prev) => ({ ...prev, [foroId]: response }));
        } catch (error) {
            console.error("Error al obtener los comentarios:", error);
        }
    };

    const handleForoClick = (foroId) => {
        if (!comentarios[foroId]) {
            fetchComentarios(foroId);
        }
        setSelectedForoId(foroId);
    };

    const handleComentar = async (idForo) => {
        if (!nuevoComentario || !fechaCreacionComentario) return;

        try {
            await EstudianteService.comentar_foro(idForo, nuevoComentario, fechaCreacionComentario);
            setNuevoComentario('');
            setFechaCreacionComentario('');
            fetchComentarios(idForo);
        } catch (error) {
            console.error("Error al comentar en el foro:", error);
        }
    };

    return (
        <div className="ver-foro-container">
            <h2>Foros de la Asignatura</h2>
            <div className="foros-grid">
                {foros.map((foro) => (
                    <Card key={foro.id} className="foro-card" onClick={() => handleForoClick(foro.id)}>
                        <CardBody>
                            <CardTitle tag="h5">{foro.titulo}</CardTitle>
                            <CardText>{foro.tema}</CardText>
                            <CardText>Fecha de Creación: {new Date(foro.fecha_creacion).toLocaleDateString()}</CardText>
                            <Button color="secondary">Ver Comentarios</Button>
                            {selectedForoId === foro.id && comentarios[foro.id] && (
                                <div className="comentarios-section">
                                    {comentarios[foro.id].map((comentario) => (
                                        <div key={comentario.id} className="comentario">
                                            <p><strong>{comentario.usuario_nombre}:</strong> {comentario.comentario}</p>
                                            <p className="fecha-comentario">{new Date(comentario.fecha_creacion).toLocaleDateString()}</p>
                                        </div>
                                    ))}
                                    <Form onSubmit={(e) => { e.preventDefault(); handleComentar(foro.id); }}>
                                        <FormGroup>
                                            <Label for="nuevoComentario">Nuevo Comentario</Label>
                                            <Input
                                                type="textarea"
                                                name="nuevoComentario"
                                                id="nuevoComentario"
                                                value={nuevoComentario}
                                                onChange={(e) => setNuevoComentario(e.target.value)}
                                                required
                                            />
                                        </FormGroup>
                                        <FormGroup>
                                            <Label for="fechaCreacionComentario">Fecha de Creación del Comentario</Label>
                                            <Input
                                                type="date"
                                                name="fechaCreacionComentario"
                                                id="fechaCreacionComentario"
                                                value={fechaCreacionComentario}
                                                onChange={(e) => setFechaCreacionComentario(e.target.value)}
                                                required
                                            />
                                        </FormGroup>
                                        <Button type="submit" color="primary">Comentar</Button>
                                    </Form>
                                </div>
                            )}
                        </CardBody>
                    </Card>
                ))}
            </div>
        </div>
    );
};

export default VerForo;
