// Date: 07/18/2021
// Note: Profesor Component: VerForos
import React, { useState, useEffect } from 'react';
import profesorService from '../../services/profesorService';

const VerForos = () => {
    const [foros, setForos] = useState([]);
    const [foroSeleccionado, setForoSeleccionado] = useState(null);
    const [comentarios, setComentarios] = useState([]);
    const [comentario, setComentario] = useState('');
    const [foroCreado, setForoCreado] = useState(false);
    const [foroEditado, setForoEditado] = useState(false);
    const [foroEliminado, setForoEliminado] = useState(false);
    const [comentarioCreado, setComentarioCreado] = useState(false);
    const [comentarioEditado, setComentarioEditado] = useState(false);
    const [comentarioEliminado, setComentarioEliminado] = useState(false);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    const user = JSON.parse(localStorage.getItem('user'));
    const authService = useAuthService();
    const history = useHistory();

    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/foros`);
                setForos(response.data);
                setLoading(false);
            } catch (error) {
                setError(error);
            }
        };
        fetchData();
    }, [foroCreado, foroEditado, foroEliminado, comentarioCreado, comentarioEditado, comentarioEliminado]);

    const handleForoClick = async (id) => {
        try {
            setLoading(true);
            const response = await axios.get(`${process.env.REACT_APP_API_URL}/foros/${id}`);
            setForoSeleccionado(response.data);
            const responseComentarios = await axios.get(`${process.env.REACT_APP_API_URL}/foros/${id}/comentarios`);
            setComentarios(responseComentarios.data);
            setLoading(false);
        } catch (error) {
            setError(error);
        }
    };

    const handleForoSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/foros`, {
                titulo: e.target.titulo.value,
                descripcion: e.target.descripcion.value,
                id_profesor: user.id
            });
            setForoCreado(true);
            setLoading(false);
        }
        catch (error) {
            setError(error);
        }
    }

    const handleForoEdit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            const response = await axios.put(`${process.env.REACT_APP_API_URL}/foros/${foroSeleccionado.id}`, {
                titulo: e.target.titulo.value,
                descripcion: e.target.descripcion.value
            });
            setForoEditado(true);
            setLoading(false);
        }
        catch (error) {
            setError(error);
        }
    }

    const handleForoDelete = async () => {
        try {
            setLoading(true);
            const response = await axios.delete(`${process.env.REACT_APP_API_URL}/foros/${foroSeleccionado.id}`);
            setForoEliminado(true);
            setLoading(false);
        }
        catch (error) {
            setError(error);
        }
    }

    const handleComentarioSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/foros/${foroSeleccionado.id}/comentarios`, {
                comentario: e.target.comentario.value,
                id_estudiante: user.id
            });
            setComentarioCreado(true);
            setLoading(false);
        }
        catch (error) {
            setError(error);
        }
    }

    const handleComentarioEdit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            const response = await axios.put(`${process.env.REACT_APP_API_URL}/foros/${foroSeleccionado.id}/comentarios/${e.target.id.value}`, {
                comentario: e.target.comentario.value
            });
            setComentarioEditado(true);
            setLoading(false);
        }
        catch (error) {
            setError(error);
        }
    }

    const handleComentarioDelete = async (id) => {
        try {
            setLoading(true);
            const response = await axios.delete(`${process.env.REACT_APP_API_URL}/foros/${foroSeleccionado.id}/comentarios/${id}`);
            setComentarioEliminado(true);
            setLoading(false);
        }
        catch (error) {
            setError(error);
        }
    }

    const handleLogout = () => {
        authService.logout();
        history.push('/login');
    }

    if (!user) {
        return <div>Cargando...</div>;
    }

    return (
        <div className="dashboard-container">
            <div className="sidebar">
                <nav className="menu">
                    <ul>
                        <li>
                            <button onClick={() => history.push('/profesor/cursos')}>Cursos</button>
                        </li>
                        <li>
                            <button onClick={() => history.push('/profesor/tareas')}>Tareas</button>
                        </li>
                        <li>
                            <button onClick={() => history.push('/profesor/foros')}>Foros</button>
                        </li>
                        <li>
                            <button onClick={handleLogout}>Cerrar sesión</button>
                        </li>
                    </ul>
                </nav>
            </div>
            <div className="content">
                <h1>Foros</h1>
                <div className="foros-container">
                    <div className="foros-list">
                        <h2>Foros</h2>
                        {loading && <div>Cargando...</div>}
                        {error && <div>{error.message}</div>}
                        <ul>
                            {foros.map((foro) => (
                                <li key={foro.id}>
                                    <button onClick={() => handleForoClick(foro.id)}>{foro.titulo}</button>
                                </li>
                            ))}
                        </ul>
                    </div>
                    <div className="foro">
                        <h2>Foro</h2>
                        {foroSeleccionado ? (
                            <div>
                                <h3>{foroSeleccionado.titulo}</h3>
                                <p>{foroSeleccionado.descripcion}</p>
                                <h4>Comentarios</h4>
                                <ul>
                                    {comentarios.map((comentario) => (
                                        <li key={comentario.id}>
                                            <p>{comentario.comentario}</p>
                                            {comentario.id_estudiante === user.id && (
                                                <div>
                                                    <form onSubmit={handleComentarioEdit}>
                                                        <input type="hidden" name="id" value={comentario.id} />
                                                        <input type="text" name="comentario" defaultValue={comentario.comentario} />
                                                        <button type="submit">Editar</button>
                                                    </form>
                                                    <button onClick={() => handleComentarioDelete(comentario.id)}>Eliminar</button>
                                                </div>
                                            )}
                                        </li>
                                    ))}
                                </ul>
                                <form onSubmit={handleComentarioSubmit}>
                                    <input type="text" name="comentario" />
                                    <button type="submit">Comentar</button>
                                </form>
                                {foroSeleccionado.id_profesor === user.id && (
                                    <div>
                                        <form onSubmit={handleForoEdit}>
                                            <input type="text" name="titulo" defaultValue={foroSeleccionado.titulo} />
                                            <input type="text" name="descripcion" defaultValue={foroSeleccionado.descripcion} />
                                            <button type="submit">Editar</button>
                                        </form>
                                        <button onClick={handleForoDelete}>Eliminar</button>
                                    </div>
                                )}
                            </div>
                        ) : (
                            <p>Selecciona un foro</p>
                        )}
                    </div>
                    <div className="crear-foro">
                        <h2>Crear Foro</h2>
                        <form onSubmit={handleForoSubmit}>
                            <input type="text" name="titulo" placeholder="Título" />
                            <input type="text" name="descripcion" placeholder="Descripción" />
                            <button type="submit">Crear</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default VerForos;
