import React, { useState, useEffect } from 'react';
import directorService from '../../services/directorService';

function CrearCursoForm() {
  const [nombre, setNombre] = useState('');
  const [grado, setGrado] = useState('');
  const [colegioId, setColegioId] = useState('');  // Estado para almacenar el colegio seleccionado
  const [colegios, setColegios] = useState([]);    // Estado para almacenar la lista de colegios

  // Función para cargar los colegios disponibles
  useEffect(() => {
    const fetchColegios = async () => {
      try {
        const response = await directorService.obtenerColegios();  // Asegúrate de tener este servicio en tu API
        setColegios(response);  // Suponiendo que la respuesta es una lista de colegios
      } catch (error) {
        console.error("Error al obtener los colegios:", error);
        // Manejar el error según lo necesites
      }
    };
    fetchColegios();
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await directorService.crearCurso({ 
        nombre, 
        grado, 
        colegio_id: colegioId  // Enviar el colegio seleccionado
      });
      setNombre('');
      setGrado('');
      setColegioId('');  // Limpiar el formulario
      alert("Curso creado con éxito");
    } catch (error) {
      console.error("Error al crear el curso:", error);
      // Mostrar mensaje de error al usuario
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="nombre">Nombre:</label>
        <input
          type="text"
          id="nombre"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="grado">Grado:</label>
        <input
          type="text"
          id="grado"
          value={grado}
          onChange={(e) => setGrado(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="colegio">Colegio:</label>
        <select
          id="colegio"
          value={colegioId}
          onChange={(e) => setColegioId(e.target.value)}
          required
        >
          <option value="">Seleccione un colegio</option>
          {colegios.map((colegio) => (
            <option key={colegio.id} value={colegio.id}>
              {colegio.nombre}
            </option>
          ))}
        </select>
      </div>
      <button type="submit">Crear Curso</button>
    </form>
  );
}

export default CrearCursoForm;
