import React, { useState, useEffect } from 'react';
import AdministradorService from '../../services/administradorService';

function VerDirectores() {
    const [directores, setDirectores] = useState([]);

    // Obtener los directores
    useEffect(() => {
        const fetchDirectores = async () => {
            try {
                const directoresData = await AdministradorService.obtenerDirectores();
                setDirectores(directoresData.directores);
            } catch (error) {
                console.error("Error al obtener los directores:", error);
                // Manejar el error
            }
        };
        fetchDirectores();
    }, []);

    // Función para manejar la eliminación de un director
    const handleDelete = async (rut) => {
        try {
            await AdministradorService.eliminarDirector(rut);
            // Eliminar el director de la lista local después de eliminarlo en la base de datos
            setDirectores(directores.filter((director) => director.rut !== rut));
            alert("Director eliminado con éxito.");
        } catch (error) {
            console.error("Error al eliminar el director:", error);
            alert("Error al eliminar el director.");
        }
    };

    // Función para manejar la modificación de un director (esto solo muestra un alert por ahora, puedes implementar un formulario)
    const handleEdit = (rut) => {
        alert(`Modificar director con RUT: ${rut}`);
        // Aquí podrías redirigir a un formulario de edición
    };

    return (
        <div>
            <h2>Directores</h2>
            <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                    <tr>
                        <th>RUT</th>
                        <th>Nombre</th>
                        <th>Apellido</th>
                        <th>Dirección</th>
                        <th>Teléfono</th>
                        <th>Correo</th>
                        <th>Fecha de Nacimiento</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {directores.map((director) => (
                        <tr key={director.rut}>
                            <td>{director.rut}</td>
                            <td>{director.nombre}</td>
                            <td>{director.apellido}</td>
                            <td>{director.direccion}</td>
                            <td>{director.telefono}</td>
                            <td>{director.correo}</td>
                            <td>{director.fecha_nacimiento}</td>
                            <td>
                                <button onClick={() => handleEdit(director.rut)}>Modificar</button>
                                <button onClick={() => handleDelete(director.rut)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default VerDirectores;
