import React, { useState, useEffect } from 'react';
import axios from 'axios';

import CrearTarea from './components/CrearTarea';
import SubirRecurso from './components/SubirRecurso';
import CalificarTarea from './components/CalificarTarea';
import TomarAsistencia from './components/TomarAsistencia';
import AsignaturasDisponibles from './components/AsignaturasDisponibles';

const ProfesorDashboard = ({ profesorId }) => {
    const [cursos, setCursos] = useState([]);
    const [mensaje, setMensaje] = useState('');

    useEffect(() => {
        obtenerCursos();
    }, []);

    const obtenerCursos = async () => {
        try {
            const response = await axios.get(`/api/profesor/${profesorId}/cursos/`);
            setCursos(response.data);
        } catch (error) {
            console.error('Error al obtener los cursos:', error);
            setMensaje('No se pudieron cargar los cursos.');
        }
    };

    return (
        <div className="profesor-dashboard">
            <h2>Dashboard del Profesor</h2>

            {mensaje && <p>{mensaje}</p>}

            <section>
                <h3>Formularios</h3>
                <AsignaturasDisponibles profesorId={profesorId} />
                <CrearTarea crearTarea={(datos) => console.log(datos)} />
                <SubirRecurso subirRecurso={(datos) => console.log(datos)} />
                <CalificarTarea calificarTarea={(id, calificaciones) => console.log(id, calificaciones)} tareaId={1} />
                <TomarAsistencia tomarAsistencia={(datos) => console.log(datos)} />
            </section>
        </div>
    );
};

export default ProfesorDashboard;
