import axios from 'axios';

const API_URL = 'http://localhost:8000/api/usuario/';

const login = async (rut, password) => {
  try {
    const response = await axios.post(API_URL + 'token/', { // O la URL que uses para obtener el token JWT
      rut,
      password,
    });
    
    // Guardar el token JWT en localStorage
    localStorage.setItem('token', response.data.access); // Asumiendo que la respuesta tiene un campo 'access' con el token
    return response.data; 
  } catch (error) {
    console.error("Error al iniciar sesión:", error);
    throw error; // Re-lanzar el error para manejarlo en el componente
  }
};

const getUserData = async () => {
  try {
    const token = localStorage.getItem('token');
    
    if (!token) {
      throw new Error('No se ha encontrado un token de acceso');
    }
    
    const response = await axios.get(API_URL + 'profile/', {
      headers: {
        'Authorization': `Bearer ${token}` // Enviar el token JWT con Bearer
      }
    });
    
    return response.data;
  } catch (error) {
    console.error("Error al obtener los datos del usuario:", error);
    throw error; // Re-lanzar el error para manejarlo en el componente
  }
};

const cerrarSesion = async () => {
  try {
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('No se ha encontrado un token de acceso');
    }

    const refreshToken = document.cookie.split('; ').find(row => row.startsWith('refresh=')).split('=')[1];

    await axios.post(API_URL + 'cerrar_sesion/', { refresh: refreshToken }, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    });

    // Eliminar los tokens del almacenamiento local y cookies
    localStorage.removeItem('token');
    document.cookie = 'csrftoken=; Max-Age=0';
    document.cookie = 'refresh=; Max-Age=0';

    alert("Sesión cerrada exitosamente");
  } catch (error) {
    console.error("Error al cerrar sesión:", error);
    throw error;
  }
};

const authService = {
  login,
  getUserData,
  cerrarSesion, // Agregar la función cerrarSesion al servicio
};

export default authService;
