import React, { useState, useEffect } from "react";
import authService from "../../services/authService";
import CursosYAsignaturas from "./CursosYAsignaturas";
import Calificaciones from "./Calificaciones";
import Asistencia from "./Asistencia";
import Recursos from "./Recursos";
import Foros from "./Foros";
import Tareas from "./Tareas";
import ReportesDeProgreso from "./ReportesDeProgreso";
import Promedios from "./Promedios";

const EstudianteDashboard = () => {
    const [selectedSection, setSelectedSection] = useState("cursos");
    const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const userData = await authService.getUserData(); // Función para obtener datos del usuario
        setUser(userData);
      } catch (error) {
        console.error("Error al obtener datos del usuario:", error);
        // Manejar el error, por ejemplo, redirigiendo al login
      }
    };
    fetchUserData();
  }, []);



  if (!user) {
    return <div>Cargando...</div>;
  }

    return (
        <div>
            <h1>Bienvenido al Dashboard del Estudiante</h1>
            <nav>
                <button onClick={() => setSelectedSection("cursos")}>Cursos</button>
                <button onClick={() => setSelectedSection("calificaciones")}>Calificaciones</button>
                <button onClick={() => setSelectedSection("asistencia")}>Asistencia</button>
                <button onClick={() => setSelectedSection("recursos")}>Recursos</button>
                <button onClick={() => setSelectedSection("foros")}>Foros</button>
                <button onClick={() => setSelectedSection("tareas")}>Tareas</button>
                <button onClick={() => setSelectedSection("reportes")}>Progreso</button>
                <button onClick={() => setSelectedSection("promedios")}>Promedios</button>
            </nav>
            <div>
                {selectedSection === "cursos" && <CursosYAsignaturas />}
                {selectedSection === "calificaciones" && <Calificaciones />}
                {selectedSection === "asistencia" && <Asistencia />}
                {selectedSection === "recursos" && <Recursos />}
                {selectedSection === "foros" && <Foros />}
                {selectedSection === "tareas" && <Tareas />}
                {selectedSection === "reportes" && <ReportesDeProgreso />}
                {selectedSection === "promedios" && <Promedios />}
            </div>
        </div>
    );
};

export default EstudianteDashboard;
