import React, { useState, useEffect } from 'react';
import profesorService from '../../services/profesorService';

const VerTareas = ({ idCursoAsignatura }) => {
    const [tareas, setTareas] = useState([]);

    useEffect(() => {
        const fetchTareas = async () => {
            try {
                const tareasData = await profesorService.obtenerTareas(idCursoAsignatura);
                setTareas(tareasData);
            } catch (error) {
                console.error("Error al obtener las tareas:", error);
            }
        };
        fetchTareas();
    }, [idCursoAsignatura]);

    return (
        <div>
            <h2>Tareas</h2>
            <ul>
                {tareas.map((tarea) => (
                    <li key={tarea.id}>
                        {tarea.nombre}
                        <button onClick={() => calificarTarea(tarea.id)}>Calificar</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

const calificarTarea = (idTarea) => {
    // Lógica para calificar tarea
};

export default VerTareas;
