import React, { useState, useEffect } from 'react';
import authService from '../../services/authService';

import CrearTarea from './CrearTarea';
import SubirRecurso from './SubirRecurso';
import CalificarTarea from './CalificarTarea';
import TomarAsistencia from './TomarAsistencia';
import AsignaturasDisponibles from './AsignaturasDisponibles';

const ProfesorDashboard = () => {
    const [usuario, setUsuario] = useState(null);
    const [asignaturas, setAsignaturas] = useState([]);
    const [asignaturaSeleccionada, setAsignaturaSeleccionada] = useState(null);

    useEffect(() => {
        setUsuario(authService.getUsuario());
    }, []);

    return (
        <div>
            <h2>Dashboard Profesor</h2>
            <h3>Bienvenido, {usuario && usuario.nombre}</h3>
            <AsignaturasDisponibles
                asignaturas={asignaturas}
                setAsignaturas={setAsignaturas}
                setAsignaturaSeleccionada={setAsignaturaSeleccionada}
            />
            {asignaturaSeleccionada && (
                <>
                    <CrearTarea asignaturaId={asignaturaSeleccionada} />
                    <SubirRecurso asignaturaId={asignaturaSeleccionada} />
                    <CalificarTarea asignaturaId={asignaturaSeleccionada} />
                    <TomarAsistencia asignaturaId={asignaturaSeleccionada} />
                </>
            )}
        </div>
    );
};

export default ProfesorDashboard;
