import React, { useEffect, useState } from "react";
import EstudianteService from "../services/estudianteService";

const Tareas = () => {
    const [tareas, setTareas] = useState([]);
    const estudianteRut = "12345678-9";

    useEffect(() => {
        const fetchTareas = async () => {
            try {
                const data = await EstudianteService.obtener_tareas(estudianteRut);
                setTareas(data);
            } catch (error) {
                console.error("Error al obtener tareas:", error);
            }
        };
        fetchTareas();
    }, []);

    return (
        <div>
            <h2>Tareas</h2>
            <ul>
                {tareas.map((tarea) => (
                    <li key={tarea.id}>
                        {tarea.titulo} - {tarea.estado ? "Completada" : "Pendiente"}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Tareas;
