import React, { useState, useEffect } from 'react';
import profesorService from '../../services/profesorService';
import { Card, CardTitle, CardText, CardBody } from 'reactstrap';
import './profesorCss/verCurso.css';

const VerCurso = ({ idAsignatura }) => {
    const [curso, setCurso] = useState(null);
    const [alumnos, setAlumnos] = useState([]);
    const [asignaturaNombre, setAsignaturaNombre] = useState("");
    const [cursoNombre, setCursoNombre] = useState("");
    const [cursoGrado, setCursoGrado] = useState("");

    useEffect(() => {
        const fetchCurso = async () => {
            try {
                const cursoData = await profesorService.obtenerCursoAsignatura(idAsignatura);
                if (cursoData) {
                    setCurso(cursoData);
                    setAsignaturaNombre(cursoData.asignatura?.nombre || 'Sin nombre');
                    setCursoNombre(cursoData.curso?.nombre || 'Sin nombre');
                    setCursoGrado(cursoData.curso?.grado || 'Sin grado');
    
                    const inscripcionesData = await profesorService.obtenerEstudiantes(cursoData.id);
                    const alumnosData = inscripcionesData.map(inscripcion => ({
                        rut: inscripcion.estudiante_rut,
                        nombre: inscripcion.estudiante_nombre,
                        apellido: inscripcion.estudiante_apellido
                    }));
                    setAlumnos(alumnosData);
                }
            } catch (error) {
                console.error("Error al obtener el curso y alumnos:", error);
            }
        };
        fetchCurso();
    }, [idAsignatura]);

    if (!curso) {
        return <div>Cargando...</div>;
    }

    return (
        <div className="curso-detalle-container">
            <h2>Libro de Clases</h2>
            <h3>{asignaturaNombre}</h3>
            <h4>{cursoNombre} - {cursoGrado}</h4>
            <Card>
                <CardBody>
                    <CardTitle tag="h5">{curso.asignatura?.nombre || 'Sin nombre'}</CardTitle>
                    <CardText>
                        Profesor: {curso.asignatura?.profesor?.nombre || 'Sin nombre'}<br />
                        Curso: {curso.curso?.nombre || 'Sin nombre'} - {curso.curso?.grado || 'Sin grado'}<br />
                        Colegio: {curso.curso?.colegio?.nombre || 'Sin nombre'}<br />
                        Cantidad de Alumnos: {alumnos.length}
                    </CardText>
                </CardBody>
            </Card>
            <div className="lista-alumnos">
                <h3>Lista de Alumnos</h3>
                <ul>
                    {alumnos.map((alumno) => (
                        <li key={alumno.rut}>{alumno.rut} - {alumno.nombre} {alumno.apellido}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default VerCurso;
