import React, { useEffect, useState } from "react";
import EstudianteService from "../services/estudianteService";

const Promedios = () => {
    const [promedios, setPromedios] = useState([]);
    const estudianteRut = "12345678-9";
    const asignaturaId = "1";
    const semestre = "2024-1";

    useEffect(() => {
        const fetchPromedios = async () => {
            try {
                const data = await EstudianteService.obtener_promedios(estudianteRut, asignaturaId, semestre);
                setPromedios(data);
            } catch (error) {
                console.error("Error al obtener promedios:", error);
            }
        };
        fetchPromedios();
    }, []);

    return (
        <div>
            <h2>Promedios</h2>
            <ul>
                {promedios.map((item, index) => (
                    <li key={index}>
                        {item.asignatura}: {item.promedio}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Promedios;
