import React, { useState, useEffect } from 'react';
import { EstudianteService } from '../../services/estudianteService';
import { Card, CardBody, CardTitle, CardText, Button } from 'reactstrap';
import './estudianteCss/verRecursos.css';

const VerRecursos = ({ asignaturaId }) => {
    const [recursos, setRecursos] = useState([]);

    useEffect(() => {
        const fetchRecursos = async () => {
            try {
                const recursosData = await EstudianteService.obtener_recursos(asignaturaId);
                setRecursos(recursosData);
            } catch (error) {
                console.error("Error al obtener los recursos:", error);
            }
        };
        fetchRecursos();
    }, [asignaturaId]);

    const handleDescargar = async (recursoId) => {
        try {
            await EstudianteService.descargar_recurso(recursoId);
            alert("Recurso descargado exitosamente");
        } catch (error) {
            console.error("Error al descargar el recurso:", error);
        }
    };

    return (
        <div className="ver-recursos-container">
            <h2>Recursos</h2>
            <div className="recursos-grid">
                {recursos.map((recurso) => (
                    <Card key={recurso.id} className="recurso-card">
                        <CardBody>
                            <CardTitle tag="h5">{recurso.nombre}</CardTitle>
                            <CardText>{recurso.descripcion}</CardText>
                            {recurso.archivo && (
                                <Button onClick={() => handleDescargar(recurso.id)} color="primary">Descargar Archivo</Button>
                            )}
                        </CardBody>
                    </Card>
                ))}
            </div>
        </div>
    );
};

export default VerRecursos;
