//LMS/lms_renca_frontend/src/components/profesor/CalificarTarea.js
import React, { useState, useEffect } from 'react';
import profesorService from '../../services/profesorService';

const CalificarTarea = ({ asignaturaId }) => {
    const [alumnos, setAlumnos] = useState([]);
    const [tareas, setTareas] = useState([]);
    const [calificaciones, setCalificaciones] = useState([]);

    useEffect(() => {
        profesorService.getAlumnos(asignaturaId).then((data) => {
            setAlumnos(data);
        });
    }, [asignaturaId]);

    useEffect(() => {
        profesorService.getTareas(asignaturaId).then((data) => {
            setTareas(data);
        });
    }, [asignaturaId]);

    const handleChange = (e, alumnoId, tareaId) => {
        const { value } = e.target;
        setCalificaciones([...calificaciones, { alumnoId, tareaId, nota: value }]);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        profesorService.calificarTarea(asignaturaId, calificaciones).then(() => {
            alert('Tareas calificadas');
            setCalificaciones([]);
        });
    };

    return (
        <div>
            <h3>Calificar Tarea</h3>
            <form onSubmit={handleSubmit}>
                {alumnos.map((alumno) => (
                    <div key={alumno._id}>
                        <h4>{alumno.nombre}</h4>
                        {tareas.map((tarea) => (
                            <div key={tarea._id}>
                                <input
                                    type="number"
                                    placeholder={tarea.titulo}
                                    onChange={(e) => handleChange(e, alumno._id, tarea._id)}
                                />
                            </div>
                        ))}
                    </div>
                ))}
                <button type="submit">Calificar Tareas</button>
            </form>
        </div>
    );
};

export default CalificarTarea;
