// LMS/lms_renca/src/components/administradorService.js
import axios from 'axios';

const BASE_URL = 'http://localhost:8000/municipalidad/';
const token = localStorage.getItem('token'); // or any other method to retrieve the token
axios.defaults.headers.common['Authorization'] = `Bearer ${token}`; // Asegúrate de establecer el token correctamente

const obtenerColegios = async () => {
    try {
        const response = await axios.get(`${BASE_URL}colegios/`);
        return response.data;
    } catch (error) {
        console.error(error);
        throw error;
    }
};

const crearColegio = async (colegio) => {
    try {
        const response = await axios.post(`${BASE_URL}colegios/`, colegio);
        return response.data;
    } catch (error) {
        console.error(error);
        throw error;
    }
};

const actualizarColegio = async (id, colegio) => {
    try {
        const response = await axios.put(`${BASE_URL}colegios/${id}/`, colegio);
        return response.data;
    } catch (error) {
        console.error(error);
        throw error;
    }
};

const eliminarColegio = async (id) => {
    try {
        const response = await axios.delete(`${BASE_URL}colegios/${id}/`);
        return response.data;
    } catch (error) {
        console.error(error);
        throw error;
    }
};

const obtenerDirectores = async () => {
    try {
        const response = await axios.get(`${BASE_URL}directores/`);
        return response.data;
    } catch (error) {
        console.error(error);
        throw error;
    }
};

const crearDirector = async (director) => {
    try {
        const response = await axios.post(`${BASE_URL}directores/`, director);
        return response.data;
    } catch (error) {
        console.error(error);
        throw error;
    }
};

const actualizarDirector = async (id, director) => {
    try {
        const response = await axios.put(`${BASE_URL}directores/${id}/`, director);
        return response.data;
    } catch (error) {
        console.error(error);
        throw error;
    }
};

const eliminarDirector = async (id) => {
    try {
        const response = await axios.delete(`${BASE_URL}directores/${id}/`);
        return response.data;
    } catch (error) {
        console.error(error);
        throw error;
    }
};

const obtenerResultados = async () => {
    try {
        const response = await axios.get(`${BASE_URL}resultados/`);
        return response.data;
    } catch (error) {
        console.error(error);
        throw error;
    }
};

export {
    obtenerColegios,
    crearColegio,
    actualizarColegio,
    eliminarColegio,
    obtenerDirectores,
    crearDirector,
    actualizarDirector,
    eliminarDirector,
    obtenerResultados
};
