import React, { useState, useEffect } from 'react';
import AdministradorService from '../../services/administradorService';

function VerDirectores() {
    const [directores, setDirectores] = useState([]);

    useEffect(() => {
        const fetchDirectores = async () => {
            try {
                const directoresData = await AdministradorService.obtenerDirectores();
                setDirectores(directoresData);
            } catch (error) {
                console.error("Error al obtener los directores:", error);
            }
        };
        fetchDirectores();
    }, []);

    const handleDelete = async (rut) => {
        try {
            await AdministradorService.eliminarDirector(rut);
            setDirectores(directores.filter((director) => director.rut !== rut));
            alert("Director eliminado con éxito.");
        } catch (error) {
            console.error("Error al eliminar el director:", error);
            alert("Error al eliminar el director.");
        }
    };

    const handleEdit = (rut) => {
        alert(`Modificar director con RUT: ${rut}`);
    };

    return (
        <Container>
            <h2>Directores</h2>
            <Row>
                {directores.map((director) => (
                    <Col key={director.rut} sm={12} md={6} lg={4} xl={3}>
                        <Card className="mb-4">
                            <Card.Body>
                                <Card.Title>{director.nombre} {director.apellido}</Card.Title>
                                <Card.Text>
                                    <strong>RUT:</strong> {director.rut}<br />
                                    <strong>Dirección:</strong> {director.direccion}<br />
                                    <strong>Teléfono:</strong> {director.telefono}<br />
                                    <strong>Correo:</strong> {director.correo}<br />
                                    <strong>Fecha de Nacimiento:</strong> {director.fecha_nacimiento}
                                </Card.Text>
                                <Button variant="primary" onClick={() => handleEdit(director.rut)}>Modificar</Button>
                                <Button variant="danger" onClick={() => handleDelete(director.rut)} className="ml-2">Eliminar</Button>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
        </Container>
    );
}

export default VerDirectores;
