import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import profesorService from '../../services/profesorService';

const CrearTarea = ({ idCursoAsignatura }) => {
    const [titulo, setTitulo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [fechaEntrega, setFechaEntrega] = useState('');
    const [archivo, setArchivo] = useState(null);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('titulo', titulo);
            formData.append('descripcion', descripcion);
            formData.append('fecha_publicacion', new Date().toISOString().split('T')[0]); // Fecha actual en formato YYYY-MM-DD
            formData.append('fecha_entrega', fechaEntrega); // Fecha de entrega sin hora
            formData.append('archivo', archivo);

            await profesorService.crearTarea(idCursoAsignatura, formData);
            // Mostrar mensaje de éxito
            toast.success('¡Tarea creada exitosamente!', {
                position: "top-right",
                autoClose: 3000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
            // Redirigir al componente VerTareas
            navigate(`/verTareas/${idCursoAsignatura}`);
        } catch (error) {
            console.error('Error al crear la tarea:', error);
            toast.error('Error al crear la tarea. Intenta nuevamente.', {
                position: "top-right",
                autoClose: 3000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }
    };

    return (
        <div>
            <ToastContainer />
            <h2>Crear Tarea</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Título:
                    <input type="text" value={titulo} onChange={(e) => setTitulo(e.target.value)} required />
                </label>
                <label>
                    Descripción:
                    <textarea value={descripcion} onChange={(e) => setDescripcion(e.target.value)} required></textarea>
                </label>
                <label>
                    Fecha de Entrega:
                    <input type="date" value={fechaEntrega} onChange={(e) => setFechaEntrega(e.target.value)} required />
                </label>
                <label>
                    Archivo:
                    <input type="file" onChange={(e) => setArchivo(e.target.files[0])} />
                </label>
                <button type="submit">Crear Tarea</button>
            </form>
        </div>
    );
};

export default CrearTarea;
