//LMS/lms_renca_frontend/src/services/directorService.js
import axios from 'axios';

const API_URL = 'http://localhost:8000/api/director/';

const obtenerToken = () => {
  const token = localStorage.getItem('token');
  if (!token) {
    throw new Error('No se ha encontrado un token de acceso');
  }
  return token;
};


const crearCurso = async (cursoData) => {
  try {
    const response = await axios.post(API_URL + 'crear_curso/', cursoData);
    return response.data;
  } catch (error) {
    console.error("Error al crear el curso:", error);
    // Mostrar un mensaje de error al usuario
    if (error.response) {
      // La solicitud se hizo y el servidor respondió con un estado de error
      // (por ejemplo, 4xx o 5xx)
      console.error("Datos de la respuesta de error:", error.response.data);
      console.error("Estado de la respuesta de error:", error.response.status);
      // Puedes mostrar un mensaje al usuario basado en el error.response.status
      if (error.response.status === 400) {
        alert("Error al crear el curso: datos inválidos.");
      } else {
        alert("Error al crear el curso. Por favor, inténtalo de nuevo más tarde.");
      }
    } else if (error.request) {
      // La solicitud se hizo pero no se recibió respuesta
      console.error("Solicitud de error:", error.request);
      alert("Error de red: No se pudo conectar con el servidor.");
    } else {
      // Algo sucedió al configurar la solicitud que provocó un error
      console.error("Error:", error.message);
      alert("Error inesperado: " + error.message);
    }
    // Puedes retornar un valor por defecto o lanzar una excepción
    // para manejar el error en el componente
    return null; // O lanzar una excepción: throw new Error("Error al crear el curso");
  }
};

const crearProfesor = async (profesorData) => {
  try {
    const response = await axios.post(API_URL + 'crear_profesor/', profesorData);
    return response.data;
  } catch (error) {
    console.error("Error al crear el profesor:", error);
    // Mostrar un mensaje de error al usuario (similar al manejo de errores en crearCurso)
    return null;
  }
};

const crearEstudiante = async (estudianteData) => {
  try {
    const response = await axios.post(API_URL + 'crear_estudiante/', estudianteData);
    return response.data;
  } catch (error) {
    console.error("Error al crear el estudiante:", error);
    // Mostrar un mensaje de error al usuario (similar al manejo de errores en crearCurso)
    return null;
  }
};

const crearAsignatura = async (asignaturaData) => {
  try {
    const response = await axios.post(API_URL + 'crear_asignatura/', asignaturaData);
    return response.data;
  } catch (error) {
    console.error("Error al crear la asignatura:", error);
    // Mostrar un mensaje de error al usuario (similar al manejo de errores en crearCurso)
    return null;
  }
};

const obtenerCursos = async () => {
  try {
    const token = localStorage.getItem('token'); // Obtener el token del localStorage
    if (!token) {
      throw new Error('No se ha encontrado un token de acceso');
    }

    const response = await axios.get(API_URL + 'cursos/', {
      headers: {
        Authorization: `Bearer ${token}` // Incluir el token en la cabecera
      }
    });
    return response.data;
  } catch (error) {
    console.error("Error al obtener los cursos:", error);
    throw error;
  }
};


const obtenerProfesores = async () => {
  try {
    const response = await axios.get(API_URL + 'profesores/');
    return response.data;
  } catch (error) {
    console.error("Error al obtener los profesores:", error);
    // Mostrar un mensaje de error al usuario (similar al manejo de errores en crearCurso)
    return [];
  }
};

const obtenerEstudiantes = async () => {
  try {
    const response = await axios.get(API_URL + 'estudiantes/');
    return response.data;
  } catch (error) {
    console.error("Error al obtener los estudiantes:", error);
    // Mostrar un mensaje de error al usuario (similar al manejo de errores en crearCurso)
    return [];
  }
};

const obtenerAsignaturas = async () => {
  try {
    const response = await axios.get(API_URL + 'asignaturas/');
    return response.data;
  } catch (error) {
    console.error("Error al obtener las asignaturas:", error);
    // Mostrar un mensaje de error al usuario (similar al manejo de errores en crearCurso)
    return [];
  }
};

const obtenerReporteAsistencia = async () => {
  try {
    const response = await axios.get(API_URL + 'reporte_asistencia/');
    return response;
  } catch (error) {
    console.error("Error al obtener el reporte de asistencia:", error);
    // Mostrar un mensaje de error al usuario (similar al manejo de errores en crearCurso)
    return null;
