import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import profesorService from '../../services/profesorService';
import { Button, Card, CardBody, CardTitle, CardText } from 'reactstrap';
import './profesorCss/verTareas.css';  // Asegúrate de crear y ajustar los estilos necesarios

const VerTareas = () => {
    const { idCursoAsignatura } = useParams(); // Obtener idCursoAsignatura de los parámetros de la URL
    const [tareas, setTareas] = useState([]);

    useEffect(() => {
        const fetchTareas = async () => {
            try {
                const tareasData = await profesorService.obtenerTareas(idCursoAsignatura);
                setTareas(tareasData);
            } catch (error) {
                console.error("Error al obtener las tareas:", error);
            }
        };
        fetchTareas();
    }, [idCursoAsignatura]);

    const handleCrearTarea = () => {
        setSubSubSelectedOption('crearTarea', idCursoAsignatura); // Asegúrate de que la opción es 'crearTarea'
    };

    const calificarTarea = (idTarea) => {
        console.log("Calificar tarea:", idTarea);
    };

    return (
        <div className="ver-tareas-container">
            <h2>Tareas Realizadas</h2>
            <Button color="primary" onClick={handleCrearTarea} style={{ marginBottom: '20px' }}>Crear Nueva Tarea</Button>
            <div className="tareas-grid">
                {tareas.map((tarea) => (
                    <Card key={tarea.id} className="tarea-card">
                        <CardBody>
                            <CardTitle tag="h5">{tarea.titulo}</CardTitle>
                            <CardText>
                                {tarea.descripcion}<br />
                                {tarea.archivo && <a href={tarea.archivo} download>Descargar Archivo</a>}<br />
                                Fecha de Publicación: {new Date(tarea.fecha_publicacion).toLocaleDateString()}<br />
                                Fecha de Entrega: {new Date(tarea.fecha_entrega).toLocaleDateString()}
                            </CardText>
                            <Button color="secondary" onClick={() => calificarTarea(tarea.id)}>Calificar</Button>
                        </CardBody>
                    </Card>
                ))}
            </div>
        </div>
    );
};

export default VerTareas;
