import React, { useState, useEffect } from 'react';
import authService from '../../services/authService';
import VerCursosEstudiante from './VerCursosEstudiante'; // Este componente mostrará los cursos del estudiante
import './estudianteCss/estudiante.css';

const EstudianteDashboard = () => {
    const [user, setUser] = useState(null);
    const [selectedOption, setSelectedOption] = useState('');
    const [currentCursoId, setCurrentCursoId] = useState(null);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const userData = await authService.getUserData();
                setUser(userData);
            } catch (error) {
                console.error("Error al obtener datos del usuario:", error);
            }
        };
        fetchUserData();
    }, []);

    const handleMenuClick = (option) => {
        setSelectedOption(option);
    };

    const handleCursoClick = (cursoId) => {
        setCurrentCursoId(cursoId);
        setSelectedOption('verCurso');
    };

    if (!user) {
        return <div>Cargando...</div>;
    }

    return (
        <div className="dashboard-container">
            <div className="sidebar">
                <nav className="menu">
                    <ul>
                        <li>
                            <button onClick={() => handleMenuClick('cursos')}>Cursos</button>
                        </li>
                    </ul>
                </nav>
            </div>
            <div className="main-content">
                <div className="header">
                    <h1>Bienvenido, {user.nombre}</h1>
                    <button onClick={authService.cerrarSesion}>Cerrar Sesión</button>
                </div>
                <div className="content">
                    {selectedOption === 'cursos' && <VerCursosEstudiante onCursoClick={handleCursoClick} />}
                    {selectedOption === 'verCurso' && <div>Detalles del Curso {currentCursoId}</div>}
                </div>
            </div>
        </div>
    );
};

export default EstudianteDashboard;
