import React, { useState, useEffect } from "react";
import AdministradorService from "../../services/administradorService";
// import './municipalidad/verColegios.css';

function VerColegios() {
    const [colegios, setColegios] = useState([]);

    // Obtener los colegios
    useEffect(() => {
        const fetchColegios = async () => {
            try {
                const colegiosData = await AdministradorService.obtener_colegios();
                setColegios(colegiosData);
            } catch (error) {
                console.error("Error al obtener los colegios:", error);
                // Manejar el error
            }
        };
        fetchColegios();
    }, []);
 
    // Función para manejar la eliminación de un colegio
    const handleDelete = async (id) => {
        try {
            await AdministradorService.eliminar_colegio(id);
            // Eliminar el colegio de la lista local después de eliminarlo en la base de datos
            setColegios(colegios.filter((colegio) => colegio.id !== id));
            alert("Colegio eliminado con éxito.");
        } catch (error) {
            console.error("Error al eliminar el colegio:", error);
            alert("Error al eliminar el colegio.");
        }
    };

    // Función para manejar la modificación de un colegio (esto solo muestra un alert por ahora, puedes implementar un formulario)
    const handleEdit = (id) => {
        alert(`Modificar colegio con ID: ${id}`);
        // Aquí podrías redirigir a un formulario de edición
    };

    return (
        <div>
            <h2>Colegios</h2>
            <table border="1" style={{ width: "100%", borderCollapse: "collapse" }}>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Nombre</th>
                        <th>Dirección</th>
                        <th>Teléfono</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {colegios.map((colegio) => (
                        <tr key={colegio.id}>
                            <td>{colegio.id}</td>
                            <td>{colegio.nombre}</td>
                            <td>{colegio.direccion}</td>
                            <td>{colegio.telefono}</td>
                            <td>
                                <button onClick={() => handleEdit(colegio.id)}>Editar</button>
                                <button onClick={() => handleDelete(colegio.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default VerColegios;
